4.5 Super_Grid v2

def super_grid_v2(shape_maker, n):
    size = 300/n
    r = size/2
    x = -150+r
    y = -150+r
    shapes = []
    for j in range(n):
        for i in range(n):
            c = shape_maker(size, row=j, column=i, n=n)|translate(x=x, y=y)
            shapes.append(c)
            x = x+size
        y = y+size
        x = -150+r
    return combine(shapes)


